// src/FAQ.js

import React, { useState } from 'react';
import '../home.css';

const FAQ = () => {
    const [activeIndex, setActiveIndex] = useState(null);

    const faqs = [
        {
            question: "What is your return policy?",
            answer: "We offer a 30-day return policy for all our products. Please contact our support team for more details."
        },
        {
            question: "How can I track my order?",
            answer: "You can track your order by logging into your account and visiting the 'Order History' section."
        },
        {
            question: "Do you offer international shipping?",
            answer: "Yes, we offer international shipping to selected countries. Please check our shipping policy for more information."
        }
    ];

    const toggleAnswer = (index) => {
        setActiveIndex(activeIndex === index ? null : index);
    };

    return (
        <div className="faq-container">
            <h1>Frequently Asked Questions</h1>
            {faqs.map((faq, index) => (
                <div className="faq-item" key={index}>
                    <button
                        className="faq-question"
                        onClick={() => toggleAnswer(index)}
                    >
                        {faq.question}
                        <span className="arrow" style={{ transform: activeIndex === index ? 'rotate(180deg)' : 'rotate(0deg)' }}>&#9660;</span>
                    </button>
                    {activeIndex === index && (
                        <div className="faq-answer">
                            <p>{faq.answer}</p>
                        </div>
                    )}
                </div>
            ))}
        </div>
    );
};

export default FAQ;
